import React from 'react'; 
import './home.css'
import ReactDOM from 'react-dom';
const Home = ({ setInputText,inputText,todos, setTodos,inputEmail ,setInputEmail,inputPlace,setInputPlace}) =>{

	const inputTextHandler = (e) =>{
	  Qconsole.log(e.target.value);
	 //setInputText(e.target.value);
	};
	const inputEmailHandler = (e) =>{
		console.log(e.target.value);
	   //setInputText(e.target.value);
	  };
	  const inputPlaceHandler = (e) =>{
		console.log(e.target.value);
	   //setInputText(e.target.value);
	  };
	const submitTodoHandler = (e)=>{
	  e.preventDefault();
	  if(inputText === ""){
		alert("please add name");
	  }
	  else if(inputEmail ===""){
		  alert("please add email");

	  }
	  else if (inputPlace === ""){
		alert("please add place");
	  }
	  else{
		setTodos([
		  ...todos,{text: inputText, email:inputEmail , place:inputPlace,id: Math.random() * 1000},
		]);
		setInputText("");
		setInputEmail("");
		setInputPlace("");
	  }



	};

	return (
	     <div>
			<h1>User Details</h1> 

		   <div className="main-agileinfo">
			<div className="agileits-top">
			<form action="" method="">
					<input className="text" value={inputText} onChange={inputTextHandler} type="text"  placeholder="Username" required=""></input>
					<input className="text" value={inputEmail} onChange={inputEmailHandler} type="text"  placeholder="Email" required=""></input>

					<input className="text" value={inputPlace} onChange={inputPlaceHandler} type="text"  placeholder="Place" required=""></input>

          <button  onClick={submitTodoHandler} type="submit">
        REGISTER
      </button>	
				</form>

			</div>
		</div>
	   </div>

	);


} 

export default Home;
