        return (incart[index].id ===i)
     });

     var newcount=a[0].count;
     if(newcount>0){
      newcount-=1;
      if(a[0].id === i){
       a[0].count=newcount;
      }
    else{
        let deldata = cartdata.filter(function (id,index) {
			return (cartdata[index].id !==i)
         });
         localStorage.setItem("cart", JSON.stringify(deldata));
         window.location.reload();

    }
   }
   localStorage.setItem('cart',JSON.stringify(a));
   window.location.reload();

    };
const increase = (i) =>{
  //  alert(i);
    var incart=JSON.parse(localStorage.getItem("cart"));
    let a = incart.filter(function (id,index) {
        return (incart[index].id ===i)
     });
     var newcount=a[0].count;
      newcount+=1;
      if(a[0].id === i){
       a[0].count=newcount;
   }
   localStorage.setItem('cart',JSON.stringify(a));
   window.location.reload();
 };
   const removeProduct = (i) =>{
        //alert(i);//id is here
        console.log('"id'+i);
        if(window.confirm("Do you want to delete this product?")){
            var cartdata=JSON.parse(localStorage.getItem("cart"));
	    let deldata = cartdata.filter(function (id,index) {
			return (cartdata[index].id !==i)
         });
         localStorage.setItem("cart", JSON.stringify(deldata));
         window.location.reload();


        //     this.setState({cart: cart});
        //     this.getTotal();

    }

    }





    var incart=JSON.parse(localStorage.getItem("cart"));
    if(incart ===null){
        alert('cart empty')
        return <h2 style={{textAlign:"center"}}>Nothings Product</h2>
    }


    else{

    return(
        <div>
            { incart.map((ite,index )=>{

         return(
          <div className="details cart" key={ite.id}>
            <img src={ite.src} alt=""/>
            <div className="box">
                <div className="row">
                    <h2>{ite.title}</h2>
                    <span>${ite.price * ite.count}</span>
                </div>
                <Colors colors={ite.colors}/>
                <p>{ite.description}</p>
                <p>{ite.content}</p>
                <div className="amount">
                    <button className="count" onClick={() => reduction(ite.id)}> - </button>
                    <span>{ite.count}</span>
                    <button className="count"  onClick={() => increase(ite.id)}> + </button>
                </div>
            </div>
            <div className="delete" onClick={() => removeProduct(ite.id)} >X</div>
        </div>
       )
    })

}

     </div>
    )



}
}
export default Cart;
