import React from 'react'; 
import './home.css'
import ReactDOM from 'react-dom';
const Home = ({ setInputText,inputText,todos, setTodos,}) =>{

	const inputTextHandler = (e) =>{
	  console.log(e.target.value);
	 //setInputText(e.target.value);
	};
	const submitTodoHandler = (e)=>{
	  e.preventDefault();
	  if(inputText === ""){
		alert("please add name");
	  }
	  else{
		setTodos([
		  ...todos,{text: inputText, completed: false, id:  Math.random() * 1000},
		]);
		setInputText("");
	  }



	};
// function Home (){ 
	return (
	     <div>
			<h1>User DETAILS</h1> 

		   <div className="main-agileinfo">
			<div className="agileits-top">
			<form action="" method="">
					<input className="text" value={inputText} onChange={inputTextHandler} type="text"  placeholder="Username" required=""></input>
					<input class="text email" type="email" name="email" placeholder="Email" required=""></input>
					<input class="text" type="text" name="Place" placeholder="Place" required=""></input>

          <button  onClick={submitTodoHandler} type="submit">
        REGISTER
      </button>	
				</form>

			</div>
		</div>
	   </div>

	);


} 

export default Home;
